	public static String decrypt(String ciphertext) throws Exception {
		byte[] cipher = StringToBytes(ciphertext);
		IvParameterSpec localIvParameterSpec = new IvParameterSpec(iv);
		SecretKeySpec localSecretKeySpec = new SecretKeySpec(desKey.getBytes(),
				baseAlgorithm);
		Cipher localCipher = Cipher.getInstance(algorithm);
		localCipher.init(2, localSecretKeySpec, localIvParameterSpec);
		byte[] plain = localCipher.doFinal(cipher);
		return new String(plain,encoding);
	}
	
	public static String decrypt(String desKey, String ciphertext) throws Exception {
		byte[] cipher = StringToBytes(ciphertext);
		IvParameterSpec localIvParameterSpec = new IvParameterSpec(iv);
		SecretKeySpec localSecretKeySpec = new SecretKeySpec(desKey.getBytes(),
				baseAlgorithm);
		Cipher localCipher = Cipher.getInstance(algorithm);
		localCipher.init(2, localSecretKeySpec, localIvParameterSpec);
		byte[] plain = localCipher.doFinal(cipher);
		return new String(plain,encoding);
	}

	private static String bytesToString(byte[] barray) {
		String str = new String("");
		char cl, cr;
		short s, sr, sl;

		for (int i = 0; i < barray.length; i++) {
			s = UByte.byteToShort(barray[i]);
			sr = (short) (s & 0x0F);
			sl = (short) (s & 0xF0);
			sl = (short) (sl >> 4);

			if (sl >= 0 && sl <= 9)
				cl = (char) ('0' + sl);
			else
				cl = (char) ('A' + sl - 10);

			if (sr >= 0 && sr <= 9)
				cr = (char) ('0' + sr);
			else
				cr = (char) ('A' + sr - 10);

			str += cl;
			str += cr;
		}
		return str;
	}
	
	private static byte[] StringToBytes(String str) {
		char l, r;
		short tv;
		byte[] b = new byte[str.length() / 2];

		for (int i = 0; i < str.length() / 2; i++) {
			l = str.charAt(i * 2);
			r = str.charAt(i * 2 + 1);

			if (l >= '0' && l <= '9')
				tv = (short) ((l - '0'));
			else if (l >= 'A' && l <= 'F')
				tv = (short) ((l - 'A') + 10);
			else if (l >= 'a' && l <= 'f')
				tv = (short) ((l - 'a') + 10);
			else
				tv = 0;

			tv = (short) (tv << 4);

			if (r >= '0' && r <= '9')
				tv |= (short) ((r - '0'));
			else if (r >= 'A' && r <= 'F')
				tv |= (short) ((r - 'A') + 10);
			else if (r >= 'a' && r <= 'f')
				tv |= (short) ((r - 'a') + 10);
			else
				tv = 0;

			b[i] = shortToByte(tv);
		}
		return b;
	}
	
	
	private static byte shortToByte(short v) {
		short value = (short) (v & 0xFF);

		byte baseB = 0;
		byte valueB = 0;

		if (value <= 127) {
			valueB = (byte) value;
			return valueB;
		}

		baseB = 1;
		baseB = (byte) (baseB << 7);
		valueB = (byte) (value - 128);
		valueB = (byte) (valueB | baseB);

		return (valueB);
	}
